    public static int threeSumMulti(int[] arr, int target) {
        long ans = 0;
        int[] hash = new int[101];
        for (int a : arr) {
            hash[a]++;
        }
        for (int i = 0; i < 101; i++) {
            for (int j = i; j < 101; j++) {
                int k = target - i - j;
                if (k > 100 || k < 0) {
                    continue;
                }
                if (i == j && j == k) {
                    ans += ((long) hash[i] * (hash[j] - 1) * (hash[k] - 2) / 6);
                } else if (i == j) {
                    ans += ((long) hash[i] * (hash[j] - 1) * hash[k] / 2);
                } else if (j < k) {
                    ans += ((long) hash[i] * hash[j] * hash[k]);
                }
            }
        }
        return (int) (ans % 1000000007);
    }
